import { useState } from "react";

function StudioCreateEdit() {
  const [formData, setFormData] = useState({
    business_name: "",
    location: "",
    blurb: "",
    image_url: "",
    format: "",
  });

  const { business_name, location, blurb, image_url, format } = formData;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div>
      <h2>Welcome! Let's build your profile.</h2>
      <form>
        <input
          type="text"
          placeholder="Studio Name"
          name="business_name"
          value={business_name}
          onChange={handleChange}
        />
        <br />
        <input
          type="text"
          placeholder="Where are you located?"
          name="location"
          value={location}
          onChange={handleChange}
        />
        <br />
        <textarea
          type="text"
          placeholder="Tell us about your gym or studio.."
          name="blurb"
          value={blurb}
          onChange={handleChange}
        />
        <br />
        <input
          type="text"
          placeholder="Insert image URL here"
          name="image_url"
          value={image_url}
          onChange={handleChange}
        />
        <label>Formats:</label>
        <div>
          <input
            type="checkbox"
            name="yoga"
            id="yoga"
            value={format}
            onClick={handleChange}
          />
          <label for="yoga">Yoga</label>
        </div>
        <div>
          <input
            type="checkbox"
            name="cardio"
            id="cardio"
            value={format}
            onClick={handleChange}
          />
          <label for="cardio">cardio</label>
        </div>
        <div>
          <input
            type="checkbox"
            name="weight-lifting"
            id="weight-lifting"
            value={format}
            onClick={handleChange}
          />
          <label for="weight-lifting">Weight Lifting</label>
        </div>

        <p>Clients can contact you here:</p>
        <p>**studio owner email**</p>
        {/* once submitted direct user to their studio's detail page */}
        <button>DONE!</button>
      </form>
    </div>
  );
}

export default StudioCreateEdit;
